module.exports = function(grunt) {
    // Preloader HTML/CSS files minification
    var preloadFilesExist = ['.html', '.css', '.js'].map(function(extension) {
        return grunt.file.exists('app/preload/preload' + extension);
    }).reduce(function(f1Exists, f2Exists) {
        return f1Exists && f2Exists;
    });

    var tasks = preloadFilesExist ? ['htmlmin', 'postcss', 'uglify'] : function() {};

    grunt.registerTask('adaptive-build_preloader', tasks);
};
